INSERT INTO `newsletter_detail` VALUES (906,16,'Votre site web en 1ère page des recherches Google !','/uploads/conference_seo.jpg','http://www.iconeo.fr/adwords-publicite/','34 millions d\'internautes français,\r\n89 % utilisent Google \r\n75% des internautes cliquent sur ces liens situés en 1ère page.\r\n\r\nICONEO, certifié Google AdWords, garanti la visibilité de votre site internet dans les première places d une recherche Google.\r\n\r\nTestez Gratuitement une campagne publicitaire Adwords sur 3 mois... Contactez nous.','bleu'),(907,16,'Envoyez des newsletters à votre image !','/uploads/newsletter_copie.jpg','http://www.iconeo.fr/contact/','Vous pourriez envoyer une newsletter à votre image à l\'ensemble de vos clients (tarifs, informations, offres,...)\r\n - sans limite du nombre de destinataires\r\n - sans limite du nombre d envois\r\n - avec un outil dédié et simple à utiliser\r\n\r\nSeulement à partir de 19€ /mois ... contactez nous pour en savoir plus.','bleu'),(908,16,'Un site web ','/uploads/montage_luberon_1b_315x0.jpg','http://www.iconeo.fr/sites-internet/','Pour vous différencier sur le web, ICONEO crée pour vous un site :\r\n\r\n  - à votre Image (unique)\r\n  - avec un référencement Google Maximal\r\n  - facile à administrer\r\n  - qui s\'adapte aux mobiles\r\n  - évolutif (vers e-commerce, e-marketing,...)','bleu'),(909,16,'TIC : Mieux Formé = Plus efficace !','/uploads/schema_formation_300x217.png','http://www.iconeo.fr/formation/','Pour mieux préparer l\'avenir : Formez vous au numérique :\r\n\r\n  - Internet, \r\n  - réseaux sociaux,\r\n  - cloud,\r\n  - graphisme,\r\n  - outils e-marketing, ...','fuschia'),(910,16,'ERP-CRM : un meilleur suivi client !','/uploads/dolibarr_partner.png','http://www.iconeo.fr/crm-gestion-commerciale/','Dolibarr ERP & CRM est un logiciel moderne de gestion de votre activité commerciale  (contacts, factures, commandes, stocks, agenda, etc…).\r\n\r\nTarif : 27€/mois sans aucun engagement... Testez le gratuitement pendant 1 mois en nous contactant !','vert'),(918,14,'Un site web adapté à votre activité viticole','/uploads/web_influe_sur_les_achats_de_vin.jpg','http://www.iconeo.fr/sites-internet/','Pour vous différencier sur le web, ICONEO crée pour vous un site :\r\n\r\n  - à votre Image (unique)\r\n  - avec un référencement Google Optimal\r\n  - facile à administrer\r\n  - qui s\'adapte aux mobiles\r\n  - évolutif (vers e-commerce, e-marketing,...)','bleu'),(919,14,'Votre site web en 1ère page des recherches Google !','/uploads/google_partner - Copy 1.jpg','http://www.iconeo.fr/adwords-publicite/','34 millions d\'internautes français,\r\n89 % utilisent Google \r\n75% des internautes cliquent sur ces liens situés en 1ère page.\r\n\r\nICONEO, certifié Google AdWords, garanti la visibilité de votre site internet dans les première places d une recherche Google.\r\n ','bleu'),(920,14,'Nouvelles technologies : Mieux Formé = Plus efficace !','/uploads/on_forme_local.jpg','http://www.iconeo.fr/formation/','Pour mieux préparer l\'avenir : Formez vous au numérique :\r\n\r\n  - Internet, \r\n  - réseaux sociaux,\r\n  - cloud,\r\n  - graphisme,\r\n  - outils e-marketing, ...','fuschia'),(921,14,'Envoyez des newsletters à votre image !','/uploads/newsletter_copie.jpg','http://www.iconeo.fr/contact/','Vous pourriez envoyer une newsletter à votre image à l\'ensemble de vos clients (tarifs, informations, offres,...)\r\n - sans limite du nombre de destinataires\r\n - sans limite du nombre d envois\r\n - avec un outil dédié et simple à utiliser\r\n\r\nSeulement à partir de 17€ /mois ... contactez nous pour en savoir plus.','bleu'),(936,20,'gbgjnvbn','/uploads/flesca.jpg','http://www.iconeo.fr/','dfhhcghjhch','bleu'),(937,15,'1 Formateur principal : Frédéric LESCA','/uploads/flesca.jpg','http://www.iconeo.fr/','Spécialités : Marketing, Bureautique, Graphisme, ERP-CRM, Web, Référencement, Graphisme, e-marketing, e-commerce, cloud, lecture rapide, mindmaping, techniques de mémorisation...\r\n\r\nIngénieur Technico-commercial, puis Directeur Commercial & marketing pendant 15 ans (groupe SFR).\r\nFormateur en \"nouvelles technologies\" depuis 25 ans. Certification pédagogique, Ingénieur Système.','fuschia'),(938,15,'2 Formateurs en poste','/uploads/Formation.jpg','http://www.iconeo.fr/','<b>Nathalie</b> (Comptabilité & gestion, droit social)\r\n15 ans chef de mission en cabinet comptable & depuis 14 ans responsable administrative et financière en entreprise.\r\n\r\n<b>Francois</b> (programmation-développement Informatique & gestion de projet)\r\n20 ans en tant qu\'architecte et directeur technique (Orange, SFR, FSecure, Novaccess)','fuschia'),(939,15,'Les TIC au service du Marketing et du commerce','/uploads/abonnes_newsletter.jpg','http://www.iconeo.fr/','<b>Programme de la conférence (3h) :</b>\r\n\r\nLes bases de données au centre de la valeur de l\'entreprise\r\nLa relation client (ERP-CRM) \r\nFidéliser ses clients avec l\'e-marketing\r\nPasser à l\'e-commerce','fuschia'),(940,15,'Le référencement, la clé de la visibilité des entreprises','/uploads/conference_seo.jpg','http://www.iconeo.fr/','<b>Programme de la conférence (3h) :</b>\r\n\r\nGoogle Search: incontournable !\r\nAnalyse de la Première page de recherche Google\r\nRéférencement naturel et payant (SEO-SEA)\r\nLes autres outils (concentrateurs, annuaires)','fuschia'),(941,15,'Les réseaux sociaux au service des entreprises','/uploads/reseaux_sociaux.png','http://www.iconeo.fr/','<b>Programme de la conférence (3h) :</b>\r\n\r\nVers de nouveaux modes de communication\r\nA chaque réseau social son utilité\r\nLes dangers et les enjeux','fuschia'),(942,15,'Le Cloud dans les entreprises','/uploads/cloud_computing.jpg','http://www.iconeo.fr/','<b>Programme de la conférence (3h) :</b>\r\n\r\n Le cloud : définition et genèse\r\n Les services proposés et les forces en présences\r\n Les enjeux technologiques, économiques et industriels','fuschia'),(943,15,'Les clés du Web','/uploads/internet.jpg','http://www.iconeo.fr/','<b>Programme de la conférence (3h) :</b>\r\n\r\nLes chiffres du web, les acteurs en présence.\r\nNoms de domaine\r\nVocabulaire & technologies (Back office, CSV, Front office, CRM, E-Marketing, Upload, URL, HTML, Base de données)\r\nLes outils (CMS ou « sur mesure »)','fuschia');
